CREATE TABLE "episode_views" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "episode_id" integer NOT NULL, "user_id" integer NOT NULL, "created_at" datetime, "updated_at" datetime);

CREATE TABLE "episodes"(
	id INTEGER NOT NULL,
	title VARCHAR(255),
	season INTEGER NOT NULL,
	episode INTEGER NOT NULL,
	url VARCHAR(255),
	thumbnail VARCHAR(255),
	summary TEXT,
	created_at DATETIME,
	updated_at DATETIME
, "rating_average" decimal(6,2) DEFAULT 0);

CREATE TABLE "rates" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "rater_id" integer, "rateable_id" integer, "rateable_type" varchar(255), "stars" integer NOT NULL, "dimension" varchar(255), "created_at" datetime, "updated_at" datetime);

CREATE TABLE "schema_migrations" ("version" varchar(255) NOT NULL);

CREATE TABLE "users" ("id" INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, "login" varchar(40), "name" varchar(100) DEFAULT '', "email" varchar(100), "crypted_password" varchar(40), "salt" varchar(40), "created_at" datetime, "updated_at" datetime, "remember_token" varchar(40), "remember_token_expires_at" datetime);

